%classdef Visibility < PlannerToolBase    properties(Access = public)           StartDate % should be in UTC         Coo    end    properties(Access = private)            end        methods  % Constructor        function Obj = Visibility(Args)            %            %            arguments                Args.TimeBin   = 0.01; % [days] 0.01 day = 864 s ~ 3 x 300 s                  Args.StartDate = [];   % should be in JD (UTC)             end            %            if ~isempty(Args.StartDate)                Obj.StartDate = Args.StartDate;            else                Obj.StartDate = celestial.time.date2jd;            end        end    end    methods        function uninterruptedVisibility(Obj, Args)            %            arguments                Obj                Args.UninterreptedDays = 180;                 Args.Coo            end        end        %        function immedeateVisibility(Obj, Args)            %            arguments                Obj                Args.Coo            end        end    end    methods(Static)        Result = debug()            % unitTest        Result = unitTest()            % unitTest    endend