%classdef Visibility < PlannerToolBase    properties(Access = public)           StartDate % should be in UTC         Coo    end    properties(Access = private)            end        methods  % Constructor        function Obj = Visibility(Args)            %            arguments                Args.TimeBin   = 0.01; % [days] 0.01 day = 864 s ~ 3 x 300 s                  Args.StartDate = [];   % should be in JD (UTC)                 Args.Coo       = [];   % a 2-column table of [RA, Dec] in [deg]            end            %            if ~isempty(Args.StartDate)                Obj.StartDate = Args.StartDate;            else                Obj.StartDate = celestial.time.date2jd;            end        end    end    %    methods % visibility functions         function showVisibility(Obj, Args)            % show periods of uniterrupted visibility             arguments                Obj                Args.Coo                Args.UninterreptedDays = [];  % days                Args.NumExp            = 1;                Args.Expo              = 300; % [s]                    Args.SearchScope       = 180; % days (search windows within this period of time)            end            %            if ~isempty(Args.Coo)                Coo = Args.Coo;            elseif ~isempty(Obj.Coo)                Coo = Obj.Coo;            else                error('No coordinates found as function input or object property');            end                        if ~isempty(Args.UninterreptedDays) % find periods in days                UDays = Args.UninterreptedDays;             else % find periods in standard exposures                UDays = Args.NumExp .* Args.Expo ./ 86400; % [days]             end                            end        %    end    methods(Static)        Result = debug()            % unitTest        Result = unitTest()            % unitTest    endend